<?phpinclude_once('m/model.php');class C_Page extends C_Base{    public function action_index()    {        $this->title .= 'Регистрация пользователя';//		$text = text_get();        $this->content = $this->Template('v/v_index.php', array('text' => $text));    }    public function action_edit()    {        $this->title .= 'Личный кабинет';        if ($this->isPost()) {            text_set($_POST['text']);            header('location: index.php');            exit();        }//		$text = text_get();        $this->content = $this->Template('v/v_edit.php', array('text' => $text));    }    public function action_vhod()    {        $this->title .= 'Вход';        if ($this->isPost()) {//            text_set($_POST['text']);            header('location: index.php?c=page&act=vhod');            exit();        }//		$text = text_get();        $this->content = $this->Template('v/v_vhod.php', array('text' => $text));    }}