<?phpfunction getAll($connect, $table, $orderby = 'id'){    $query = "SELECT * FROM {$table} order by {$orderby} desc";    $result = mysqli_query($connect, $query);    if (!$result)        die(mysqli_error($connect));    $n = mysqli_num_rows($result);    $res = array();    for ($i = 0; $i < $n; $i++) {        $row = mysqli_fetch_assoc($result);        $res[] = $row;    }    return $res;}function newUser($connect, $name, $login, $email, $pass){    $t = "INSERT INTO users (name, login, email, pass) VALUES ('%s','%s','%s','%s')";    $query = sprintf($t, mysqli_real_escape_string($connect, $name), mysqli_real_escape_string($connect, $login), mysqli_real_escape_string($connect, $email), mysqli_real_escape_string($connect, $pass));    $result = mysqli_query($connect, $query);    if (!$result) {        die(mysqli_error($connect));    }    return true;}require_once "config/database.php";if (isset($_POST[submit])) {    $name = trim(strip_tags($_POST[name]));    $login = trim(strip_tags($_POST[login]));    $user = getAll($connect, 'users');    if (strtolower($login) == 'admin') {        exit("Логин админа нельзя зарегистрировать!");    }    foreach ($user as $item) {        if ($login == $item[login]) {            exit("Такой уже логин есть!");        }    }    if (filter_var($_POST[email], FILTER_VALIDATE_EMAIL)) {        $email = trim(strip_tags($_POST[email]));    }    $pass = trim(strip_tags($_POST[pass]));    newUser($connect, $name, $login, $email, md5($pass));    $message = "Вы зарегистрированы!";}if (isset($_POST[enter])) {    $login = trim(strip_tags($_POST[login]));    $pass = trim(strip_tags($_POST[pass]));    $user = getAll($connect, 'users');    foreach ($user as $item) {        if ($login == $item[login] and md5($pass) == $item[pass]) {            $message = "Вы вошли!";            $_SESSION[login] = $login;            $_SESSION[pass] = $pass;        } else {            $message = "Не правильно ввели данные!";        }    }}if (isset($_POST['exit'])) {    unset($_SESSION['login']);    unset($_SESSION['pass']);    session_destroy();    header('Location: index.php');}